Content.Code:
_pause();

t = 0.0;
thetaSystem = 0.0;

cosineLaunchLatitudeRad = Math.cos(Math.toRadians(launchLatitude));
sineLaunchLatitudeRad = Math.sin(Math.toRadians(launchLatitude));	
cosineLaunchElevationRad = Math.cos(Math.toRadians(launchElevation));
sineLaunchElevationRad = Math.sin(Math.toRadians(launchElevation));
cosineLaunchDirectionRad = Math.cos(Math.toRadians(launchDirection));
sineLaunchDirectionRad = Math.sin(Math.toRadians(launchDirection));
/*
- The null orientation of 'Elevation' is parallel to the surface: positive elevation 
     is shooting upward, negative elevation is shooting down.
- The null orientation of 'Direction' is eastward, parallel to the plane of the equator.
    Hence a direction around zero degrees is shooting forward, and a direction in 
    the neighbourhood of 180 or -180 is shooting backward. */

/* Two components contribute to velocity in y-direction:
    a)  participating in the rotation of the planet prior to launch
    b)  the launch itself */
vyRotation = cosineLaunchLatitudeRad * (omegaSystem * (R * (1.0 + launchHeight)));
vyLaunch =  cosineLaunchDirectionRad * cosineLaunchElevationRad * launchSpeed;

/* The launch can be from any latitude. In this calculation the velocity components are
first calculated as if the launch is at the Equator, then those velocity components
are transformed to the actual latitude.
Velocity components in x- and z-direction at Equator (prior to adjustment for latitude) */
vxEquator = sineLaunchElevationRad * launchSpeed;
vzEquator = sineLaunchDirectionRad * cosineLaunchElevationRad * launchSpeed;

x = cosineLaunchLatitudeRad * (1.0 + launchHeight) * R;
vx =  cosineLaunchLatitudeRad * vxEquator  -  sineLaunchLatitudeRad * vzEquator ;
y = 0.0;
vy = vyRotation + vyLaunch;
z = sineLaunchLatitudeRad * (1.0 + launchHeight) * R;
vz =  sineLaunchLatitudeRad * vxEquator  +  cosineLaunchLatitudeRad * vzEquator ;

// Java Object for the rotation of the tangent great circle in the co-rotating view
rotationTangent= org.opensourcephysics.numerics.Matrix3DTransformation.rotationY(-Math.toRadians(launchLatitude));

_view.resetTraces();


if (instantPlay == true)
  {
  _play();
  }

EventContent.ZeroCondition:
if (impact == true){
return (x*x + y*y + z*z) - RSquared;
}
else return 1.0;

Content.Code:
// Constant rotation rate of the system as a whole
thetaSystem = omegaSystem * t;

// Java Object for the rotation of the sphere in the inertial view
rotation = org.opensourcephysics.numerics.Matrix3DTransformation.rotationZ(thetaSystem);

// Preparing the auxillary variable distanceToCenter
distanceToCenterUnit = Math.sqrt(x*x + y*y + z*z) / R;

// The groundtrack of the object's ballistic/orbital trajectory, inertial coordinate system
xGroundtrack = x / distanceToCenterUnit;
yGroundtrack = y / distanceToCenterUnit;
zGroundtrack = z / distanceToCenterUnit;


//preparing cosine and sine for transformation to corotating system
cosineTheta = Math.cos(thetaSystem);
sineTheta = Math.sin(thetaSystem);

// transforming particle position to co-rotating coordinate system
xCoro = cosineTheta * x  +  sineTheta * y;
yCoro = - sineTheta * x  + cosineTheta * y;

// The groundtrack of the object's ballistic/orbital trajectory, corotating coordinate system
xGroundtrackCoro = xCoro / distanceToCenterUnit;
yGroundtrackCoro = yCoro / distanceToCenterUnit;

// Current height above the surface
altitude = (distanceToCenterUnit - 1.0);

if (towerVisible == true) {
  towerPositionIX = cosineTheta * cosineLaunchLatitudeRad * R;
  towerPositionIY = sineTheta * cosineLaunchLatitudeRad * R;
  towerPositionIZ = sineLaunchLatitudeRad * R;
  towerSizeIX = cosineTheta * cosineLaunchLatitudeRad * launchHeight * R;
  towerSizeIY = sineTheta  * cosineLaunchLatitudeRad * launchHeight * R;
  towerSizeIZ = sineLaunchLatitudeRad * launchHeight * R;

  towerPositionCX = cosineLaunchLatitudeRad * R;
  towerPositionCY = 0.0;
  towerPositionCZ = sineLaunchLatitudeRad * R;
  towerSizeCX = cosineLaunchLatitudeRad * launchHeight * R;
  towerSizeCY = 0.0;
  towerSizeCZ = sineLaunchLatitudeRad * launchHeight * R;
}

public void heightAction () {
  if (launchHeight > 2*R) {
    towerVisible = false;
  }
  _initialize();
}

public void missilePresetAction () {
  tracesVisible = true;
  groundtrackVisible = true;
  towerVisible = false;

  omegaSystem = 0.4;
  G = 0.05;
  launchLatitude = 45.0;
  launchHeight = 0.0;
  launchSpeed = 0.2;
  launchElevation = 85.0;
  launchDirection = 180.0;
  _initialize();
}


