//convert degree values to radians
lonPE = Math.PI*lonPEdeg/180.0;
lonAN = Math.PI*lonANdeg/180.0;
incl = MoonInclineAngleRadians;
arg = Math.PI*argDeg/180.0;

//compute radial coordinate for Earth
rE = aE*(1-eE*eE)/(1+eE*Math.cos(phiE));

//compute x and y coordinates for Earth
xE = rE*Math.cos(phiE+lonPE);
yE = rE*Math.sin(phiE+lonPE);

//compute radial coordinate for planet
r = a*(1-e*e)/(1+e*Math.cos(phi));

//compute x and y coordinates for planet in its orbital plane
x = r*Math.cos(phi+arg);
y = r*Math.sin(phi+arg);

//compute x,y,z coordinates for moon
xM = -y*Math.cos(lonAN)-x*Math.cos(incl)*Math.sin(lonAN);
yM = x*Math.cos(incl)*Math.cos(lonAN)-y*Math.sin(lonAN);
zM = x*Math.sin(incl);

//compute longitude and latitude of moon
lon = Math.atan2(yM,xM);
lat = Math.atan2(zM,Math.sqrt((xM)*(xM)+(yM)*(yM)));

//compute x,y,z coordinates for ecliptic moon
xMe = -y*Math.cos(lonAN + Math.PI*0.11299435*t)-x*Math.cos(incl)*Math.sin(lonAN + Math.PI*0.11299435*t);
yMe = x*Math.cos(incl)*Math.cos(lonAN + Math.PI*0.11299435*t)-y*Math.sin(lonAN + Math.PI*0.11299435*t);
zMe = x*Math.sin(incl);

//compute longitude and latitude of ecliptic moon
lonE = Math.atan2(yMe,xMe);
latE = Math.atan2(zMe,Math.sqrt((xMe)*(xMe)+(yMe)*(yMe)));

//compute components of line-of-sight vector
xlos = scale*Math.cos(latE)*Math.cos(lonE);
ylos = scale*Math.cos(latE)*Math.sin(lonE);
zlos = scale*Math.sin(latE);

//set the correct position of Earth shadow
if (lonS >= 0)
{
lunarEclipse = lonS - Math.PI;
proximityEM = (lunarEclipse + lonE)*(lunarEclipse + lonE);
}
else
{
lunarEclipse = lonS + Math.PI;
proximityEM =(lunarEclipse + lonE)*(lunarEclipse + lonE);

}


//show Earth shadow only when needed
if (proximityEM < 0.1225 && 36*lat*lat < 0.1)
{
 showEarth=true;
}

else 
{
  showEarth=false;
}

//compute longitude of Sun (sign reversed)
lonS = -Math.atan2(-yE,-xE);
//compute distance from Earth to planet
dist = Math.sqrt((xM-xE)*(xM-xE)+(yM-yE)*(yM-yE)+zM*zM);
//check to see if Sun is closer to Earth than planet
if (rE < dist) {
  sunFront=true;
}
else {
  sunFront=false;
}

//convert incl angle to radians
MoonInclineAngleRadians = inclDeg*Math.PI/180;

//determine where the Moon is relative to Sun and Earth
ang = lonS+lonE;

//determine if Moon is C or D shaped reative to Earth and Sun position
posMoon = ylos*Math.cos(phiE)-xlos*Math.sin(phiE);

//set polygon for shadow in Ecliptic View
if (posMoon < 0)

{
  rotation = -5*Math.sin(lat);
for (int i = 0; i < np; i++)
{
  theta = i*Math.PI/(np-1) - Math.PI/2.0;
  pv[i][0] = Math.cos(ang)*Math.cos(theta);
  pv[i][1] = Math.sin(theta);
  }
for (int i = np; i < 2*np; i++)
{
  theta = Math.PI/2.0 - (i-np)*Math.PI/(np-1);
  pv[i][0] = -Math.cos(theta);
  pv[i][1] = Math.sin(theta);
  }
  }
else
{
  rotation = 5*Math.sin(lat);
  for (int i = 0; i < np; i++)
{
  theta = i*Math.PI/(np-1) + Math.PI/2.0;
  pv[i][0] = -Math.cos(theta);
  pv[i][1] = Math.sin(theta);

  }
for (int i = np; i < 2*np; i++)
{
  theta = 3.0*Math.PI/2.0 - (i-np)*Math.PI/(np-1);
  pv[i][0] = Math.cos(ang)*Math.cos(theta);
  pv[i][1] = Math.sin(theta);
  }
  }



//change transparency of darknessPolygon to emulate Earthlight
transp = 200-(int)(20*Math.cos(ang));
color = new Color(0,0,0,transp);

//sets the visibility of the TOP stars that are in Earth's shadow

  for (int i = 0; i < nES; i++) {

   if (evStars && showEarth && Math.abs(latES[i]) <  0.3 && lonES[i] <  lunarEclipse + 0.3 && lonES[i] >  lunarEclipse - 0.3 && Math.sqrt((lonES[i] + lonE)*(lonES[i] + lonE) + (latES[i] - latE*6)*(latES[i] - latE*6)) > 0.05 ) {
