//sets scale for Orbit Frame and rescales celestial sphere
public void scaleStars () {
  for (int i=0; i<nst; i++) {
    scale = 1.5*Math.max(a/(1-e*e),aE/(1-eE*eE));
    xst[i] = scale*xstar[i];
    yst[i] = scale*ystar[i];
    zst[i] = scale*zstar[i];
    magst[i] = scale*magstar[i];}
 // _view.orbitDrawingPanel3D.setScaleFactor(scale,scale,scale);
}

//custom calls to set parameter values for moon
public void selectMoon () {
  userDef=false;
  a = 0.00257;
  e = 0.054;
  inclDeg = MoonInclineAngle;
  lonANdeg = 90.0;
  argDeg = 180.0;
  _view.resetTraces();
  scaleStars();

}
