Content.Code:
public void rays () {

f = -displayf+xmirror;
R = 2*f;
Rsq = R*R;
xobject = -displayxobject;
dobject = xobject + xmirror;
dimage = dobject*f/(dobject-f);
hi = -ho*dimage/dobject;
ximage = dimage-xmirror;

if (idealFlag)
{
  ray2x = Math.sqrt(Rsq-ho*ho)-R+xmirror;
  ray2slope = (ho-hi)/(ray2x-(xmirror-0.5*R)+(displayf+ximage));
}
else
{
  ray2x = Math.sqrt(Rsq-ho*ho)-R+xmirror;
  ray2slope = Math.tan(2*Math.atan(ho/(ray2x-(xmirror-R))));
}

if (dimage < 0) 
{
  virtual = true;
}
else
{
  virtual = false;
}

if ((displayxobject > displayf) && (displayxobject < xmirror))
{
  ray2cColor = ray1cColor = java.awt.Color.black;
}
else
{
  ray1cColor = java.awt.Color.blue;
  ray2cColor = java.awt.Color.red;
}

if (displayxobject < xmirror)
{
  ray1bColor = java.awt.Color.blue;
  ray2bColor = java.awt.Color.red;
}
else
{
  ray2bColor = ray1bColor = java.awt.Color.black;
}

if (displayxobject < xmirror)
{
  rayline2Color = java.awt.Color.green;
  rayline3Color = java.awt.Color.black;
}
else
{
  rayline2Color = java.awt.Color.black;
  rayline3Color = java.awt.Color.green;
}

for (int i = 0; i < n; i++)
{
x[i] = displayxobject;
y[i] = ho;
}

mirrorpointsx[0] = xmirror;
mirrorpointsy[0] = 0.0;
mirrorslopex[0] = 1.0;
mirrorslopey[0] = 0.0;

thetamax = Math.atan(mirrorheight/Math.sqrt(Rsq-mirrorheight*mirrorheight));
maxindex = (n-1)/2;

for (int i = 1; i <= maxindex; i++)
{
mirrorslopex[i] = Math.cos(thetamax*i/maxindex);
mirrorslopex[i+maxindex] = mirrorslopex[i];
mirrorslopey[i] = Math.sin(thetamax*i/maxindex);
mirrorslopey[i+maxindex] = -mirrorslopey[i];
mirrorpointsx[i] = xmirror-R+R*mirrorslopex[i];
mirrorpointsx[i+maxindex] = mirrorpointsx[i];
mirrorpointsy[i] = R*mirrorslopey[i];
mirrorpointsy[i+maxindex] = -mirrorpointsy[i];

}

for (int i = 0; i < n; i++)
{
vx[i] = mirrorpointsx[i]-x[i];
vy[i] = mirrorpointsy[i]-y[i];
line1x[i] = vx[i];
line1y[i] = vy[i];
if (idealFlag)
{
  vx2[i] = mirrorpointsx[i]+ximage;
  vy2[i] = mirrorpointsy[i]-hi;
  double amp = Math.sqrt(vx2[i]*vx2[i]+vy2[i]*vy2[i]);
  vx2[i] = vx2[i]/amp;
  vy2[i] = vy2[i]/amp;
  alpha = Math.atan(vy2[i]/vx2[i]);
}
else
{
  double amp = Math.sqrt(vx[i]*vx[i]+vy[i]*vy[i]);
  vx[i] = vx[i]/amp;
  vy[i] = vy[i]/amp;
  sintheta = vx[i]*mirrorslopex[i] + vy[i]*mirrorslopey[i];
  costheta = vx[i]*mirrorslopey[i] - vy[i]*mirrorslopex[i];
  alpha = Math.atan(vy[i]/vx[i]);
  theta = Math.atan(sintheta/costheta);
  alpha = alpha + 2*Math.PI - 2*theta;
}
line2x[i] = -20.0 - xmirror;
line3x[i] = 20.0 - xmirror;
line2y[i] = line2x[i]*Math.sin(alpha)/Math.cos(alpha);
line3y[i] = line3x[i]*Math.sin(alpha)/Math.cos(alpha);
}
}

Osejs.View.Creation.Element.Property:
if (ho > mirrorheight) ho = mirrorheight;
if (ho < -mirrorheight) ho = -mirrorheight;
rays();

zero=0.0;
if ((xmirror - displayf) < minf) displayf = xmirror - minf;
rays();
