for (i = 0; i < numTicks; i++)
{
  xTicks[i] = -0.8 + i*0.1;
}

y = y0;
a = -k*x/m;
PE = 0.5*k*x*x;
KE = 0.5*m*v*v;
a2 = -k2*x2/m2;
PE2 = 0.5*k2*x2*x2;
KE2 = 0.5*m2*v2*v2;
x3 = Lpendulum*Math.sin(theta);
y3 = y03-Lpendulum*Math.cos(theta);
alpha = -g*Math.sin(theta)/Lpendulum;
PE3 = m3*g*(y3+Lpendulum-y03);
KE3 = 0.5*m3*Lpendulum*Lpendulum*omega*omega;


Fg = -m*g;
FN = m*g;
Fspring = -k*x;
Fr = -b*v*Math.abs(v);

Fg2 = -m2*g;
FN2 = m2*g;
Fspring2 = -k2*x2;
Fr2 = -b2*v2*Math.abs(v2);

Fg3 = -m3*g;
Fgrad = Fg3*Math.cos(theta);
Fgtan = Fg3*Math.sin(theta);
FT = -Fgrad + m3*Lpendulum*omega*omega;

public void restart () {

x = x0;
v = 0.0;
t = 0.0;

x2 = x02;
v2 = 0.0;

theta = angle*Math.PI/180.0;
omega = 0.0;
x3 = Lpendulum*Math.sin(theta);
y3 = y03 - Lpendulum*Math.cos(theta);

_view.resetTraces();
PE = 0.5*k*x*x;
KE = 0.5*m*v*v;
newEMax = 1.1*(PE+KE);
if (newEMax > EMax) EMax = newEMax;
PE2 = 0.5*k2*x2*x2;
KE2 = 0.5*m2*v2*v2;
}

