correct2 = "  ?";
correct2background = backColor;

}

public void resetVectors () {

Length = 2.0+8.0*Math.random();
theta = 2.0*Math.PI*Math.random();
angle = theta*180.0/Math.PI;

xComp = Length*Math.cos(theta);
yComp = Length*Math.sin(theta);


}

